# Write your MySQL query statement below
SELECT C1.visited_on, SUM(C2.amount) AS amount, ROUND( SUM(C2.amount)/7 , 2 ) AS average_amount FROM (SELECT DISTINCT visited_on FROM Customer WHERE STR_TO_DATE( visited_on , '%Y-%m-%d')  > (SELECT ADDDATE( MIN(visited_on), INTERVAL 5 DAY)  FROM Customer)) AS C1 JOIN Customer C2 ON STR_TO_DATE( C2.visited_on , '%Y-%m-%d') BETWEEN SUBDATE(C1.visited_on, INTERVAL 6 DAY)  AND STR_TO_DATE( C1.visited_on , '%Y-%m-%d') GROUP BY C1.visited_on ORDER BY C1.visited_on ASC ;